import subprocess

OUTPUT_PATH = Path(__file__).parent
ASSETS_PATH = OUTPUT_PATH / r"assets\frame0"


def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)


cookie = ""
check = True
uncheck = False
force = False
verbose = False

window = Tk()

