def invert_check():
    global check
    check = not check
    button_2.config(image=button_image_2_on if check else button_image_2)


def invert_uncheck():
    global uncheck
    uncheck = not uncheck
    button_3.config(image=button_image_3_on if uncheck else button_image_3)


def invert_force():
    global force
    force = not force
    button_4.config(image=button_image_4_on if force else button_image_4)


def start_scanning():
    global cookie
    cookie = entry_1.get().strip()
    command = "main.py"
    if cookie != "":
        command += f' --cookie "{cookie}"'
    if check:
        command += " -c"
    if uncheck:
        command += " -u"
    if force:
        command += " -fd"
    subprocess.Popen(command, shell=True)


